mapper.select:
/** 게시판의 리스트 조회*/ SELECT board_id as boardId ,board_type as boardType ,title as title
,content as content
,view_count as viewCount ,start_ymd as startYmd ,end_ymd as endYmd ,status as status ,create_on as createOn ,create_by as createBy ,( select count(*) from board_file_match where board_id = A.board_id ) AS attached_file_count FROM board A WHERE 1=1
ORDER BY boardId desc
OFFSET #{pageAttr.offset} LIMIT #{pageAttr.limit}

mapper.insert:
/** 게시판 추가*/
INSERT INTO public.board (
   board_id, board_type, title, content, view_count, start_ymd, end_ymd, status,  create_by
 ) VALUES (
   #{boardId}, #{boardType}, #{title}, #{content}, #{viewCount}, replace(#{startYmd},'-',''), replace(#{endYmd},'-',''), COALESCE(#{status},'0'),  #{createBy}
 )

    /** 게시판 태그 추가 */
	INSERT INTO public.board_tag
		(tag_id, "name")
	VALUES
		(#{tagId}, #{name});

  	/** 게시판 첨부파일  추가 */
  	INSERT INTO public.board_file
	(board_file_id, phy_folder, phy_name, org_name, mime_type, file_size, ext, note, create_on, create_by)
	VALUES
	(#{boardFileId}, #{phyFolder},  #{phyName}, #{orgName}, #{mimeType}, #{fileSize}, #{ext}, #{note}, CURRENT_TIMESTAMP, #{createBy});

mapper.select:
  /* 1개의 boardTag 조회*/
	select 
		tag_id as tagId, 
		name 
	from public.board_tag 
	where 1=1

		and name = #{name}


		tag_id = #{tagId}

mapper.delete:
/** board_file_match에서 file_id로 삭제*/
delete from 
	board_file_match 
where 
	board_file_id = #{value}

/** board_tag_match에서 board_id로 삭제*/
delete from 
	board_tag_match 
where 
	board_id = #{value}
