mapper.select:
WITH RECURSIVE subfolders (node_id, parent_id, owner_id, node_name, create_on, create_by, level, path, cycle) AS( SELECT f.node_id, f.parent_id, f.owner_id, f.node_name, f.create_on, f.create_by, 0 AS level, ARRAY[f.node_id], false FROM public.fb_node f WHERE 1=1 AND parent_id = 0 AND node_type = 'D'
AND owner_id = #{ownerId}
UNION ALL SELECT d.node_id, d.parent_id, d.owner_id, d.node_name, d.create_on, d.create_by, level + 1, path || d.node_id, d.node_id = ANY(path) FROM public.fb_node d INNER JOIN subfolders s ON d.parent_id = s.node_id AND d.node_type = 'D' AND d.node_name != 'ROOT'
AND d.owner_id = #{ownerId}
AND NOT CYCLE ) -- SELECT * FROM subfolders; SELECT node_id ,parent_id ,owner_id ,node_name ,create_on ,create_by ,level ,path FROM subfolders ORDER BY path

mapper.update:
 /* renameFbNode : node의 이름을 바꾼다.*/
	update 
		public.fb_node 
	set 
		node_name = #{nodeName} 
	where 
		node_id = #{nodeId}

mapper.delete:
/* deleteNode : node를 1개 지움*/
delete from fb_node 
where node_id  = #{value};

mapper.update:
/* moveNode : parent id를 새로 설정 */
update public.fb_node 
set parent_id = #{parentId}
where node_id = #{nodeId}
